import { useState, useEffect } from 'react';
import React from 'react';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar';
import logo from './syncreon logo.png';
import Card from 'react-bootstrap/Card';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import '../src/OrderDetails.css';

function OrderDetails() {
  const [orderDetails, setOrderDetails] = useState([]);

  useEffect(() => {
    fetch('http://localhost:4000/api/getAllOrders')
      .then(response => response.json())
      .then(data => {
        setOrderDetails(data)
      });
  }, []);

  const renderOrderCards = () => {
    return orderDetails.map(order => {
      const address = `${order.address.name}\n${order.address.line1}\n${order.address.line2}`
      const customerName = `${order.customer.firstName} ${order.customer.lastName}`
      const customerPhone = order.customer.phoneNumber
      const customerEmail = order.customer.email


      return <Card>
          <Row>
            <Col xs={12} md={6}>
              <h4>Order {orderDetails.indexOf(order) + 1}</h4>
            </Col>
            <Col xs={12} md={6}>

            </Col>
        </Row>
      </Card>

      return <div className="card" style={cardStyle}>
      <div className="card header">{orderDetails.indexOf(order) + 1}</div>
      <div className="card-body">
      <h5 className="card-title">Customer Details</h5>
      <p className="address">{customerName}<br/>{customerPhone}<br/>{customerEmail}</p>
      <h6 className="card-subtitle ">Address:</h6>
      <p className="address">{address}</p>
      <Button className='my-button'>Edit</Button>
      </div>
    </div>
    })
  }

  const cardStyle = {
    backgroundColor: '#f0f0f0',
    borderRadius: '10px',
    boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.25)',
    padding: '20px',
    width: '400px',
    height: '200px',
  };
  const navbarStyle = {
    backgroundColor:'grey',paddingTop:'5px',display: 'flex',alignItems:'center',justifyContent:'center'
  };

  return (
    <div>
      <Navbar style={navbarStyle}>
        <Container>
            <img
              src={logo}
              width="210"
              height="100"
              className="d-inline-block align-top"
            />
        </Container>
      </Navbar>
      <Container>
        <h4>Orders</h4>
      <div>
        {renderOrderCards()}
      </div>
      </Container>
      {/* <Row xs={2} md={2} className="g-4">
      {Array.from({ length: 4 }).map((_, idx) => (
        <Col md={{ span: 6, offset: 3 }} >
          <Card style={{ width: '18rem', height:'20 rem'}}>
            <Card.Img variant="top" src="holder.js/100px160" />
            <Card.Body>
              <Card.Title>Card title</Card.Title>
              <Card.Text>
                This is a longer card with supporting text below as a natural
                lead-in to additional content. This content is a little bit
                longer.
              </Card.Text>
            </Card.Body>
          </Card>
        </Col>
      ))}
    </Row> */}

      {/* <ul>
        {orderDetails.map(order => (
          <li key={order.id}>
            <p>Order ID: {order.id}</p>
            <p>Customer Name: {order.customerName}</p>
            <p>Order Date: {order.orderDate}</p>
            <p>Order Total: {order.orderTotal}</p>
          </li>
        ))}
      </ul> */}
    </div>
  );
}

export default OrderDetails;
