import { useState, useEffect } from 'react';
import React from 'react';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar';
import logo from './syncreon logo.png';
import Card from 'react-bootstrap/Card';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import ListGroup from 'react-bootstrap/ListGroup'
import '../src/OrderDetails.css';

function OrderDetails() {
  const [orderDetails, setOrderDetails] = useState([]);

  useEffect(() => {
    fetch('http://localhost:4000/api/getAllOrders')
      .then(response => response.json())
      .then(data => {
        setOrderDetails(data)
      });
  }, []);

  const openEditModal = (order) => {

  }

  const renderOrderCards = () => {
    return orderDetails.map(order => {
      const customerAddress = `${order.address.name}\n${order.address.line1}\n${order.address.line2}`
      const customerName = `${order.customer.firstName} ${order.customer.lastName}`
      const customerPhone = order.customer.phoneNumber
      const customerEmail = order.customer.email

      return <Card className='p-4 mb-4'>
          <Row>
            <Col xs={12} className='mb-3'>
              <h5>Order {orderDetails.indexOf(order) + 1}</h5>
            </Col>
            <Col xs={12} md={3}>
              <h6>Customer Details</h6>
              <p>{customerName}<br/>{customerPhone}<br/>{customerEmail}</p>
            </Col>
            <Col xs={12} md={3}>
              <h6>Address</h6>
              <p>{customerAddress}</p>
            </Col>
            <Col xs={12} md={3}>
              <h6>Orders</h6>
              <ListGroup>
              {
                order.orderList.map(item => {
                  return <ListGroup.Item>
                      <h7><b>{item.itemNumber}</b></h7>
                      <p>{item.itemDescription}</p>
                    </ListGroup.Item>
                })
              }
            </ListGroup>
            </Col>
            <Col xs={12} className="justify-items-right">
              <Button className='btn-primary' onClick={openEditModal(order)}>UPDATE</Button>
            </Col>
        </Row>
      </Card>
    })
  }

  const cardStyle = {
    backgroundColor: '#f0f0f0',
    borderRadius: '10px',
    boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.25)',
    padding: '20px',
    width: '400px',
    height: '200px',
  };
  const navbarStyle = {
    backgroundColor:'grey',paddingTop:'5px',display: 'flex',alignItems:'center',justifyContent:'center'
  };

  return (
    <div>
      <Navbar style={navbarStyle}>
        <Container>
            <img
              src={logo}
              width="210"
              height="100"
              className="d-inline-block align-top"
            />
        </Container>
      </Navbar>
      <Container>
        <h4 className='mt-4 mb-4'>Orders</h4>
        <div>
          {renderOrderCards()}
        </div>
      </Container>
      {/* <Row xs={2} md={2} className="g-4">
      {Array.from({ length: 4 }).map((_, idx) => (
        <Col md={{ span: 6, offset: 3 }} >
          <Card style={{ width: '18rem', height:'20 rem'}}>
            <Card.Img variant="top" src="holder.js/100px160" />
            <Card.Body>
              <Card.Title>Card title</Card.Title>
              <Card.Text>
                This is a longer card with supporting text below as a natural
                lead-in to additional content. This content is a little bit
                longer.
              </Card.Text>
            </Card.Body>
          </Card>
        </Col>
      ))}
    </Row> */}

      {/* <ul>
        {orderDetails.map(order => (
          <li key={order.id}>
            <p>Order ID: {order.id}</p>
            <p>Customer Name: {order.customerName}</p>
            <p>Order Date: {order.orderDate}</p>
            <p>Order Total: {order.orderTotal}</p>
          </li>
        ))}
      </ul> */}
    </div>
  );
}

export default OrderDetails;
